import * as types from 'constants/types';

const signInWithEmailAndPassword = (email, password) => (dispatch) => {

    dispatch({
        type: types.auth.signIn.request,
    });

    try {
        const user = {
            email,
            password,
        };

        dispatch({
            type: types.auth.signIn.success,
            payload: user,
        });
    } catch (error) {
        dispatch({
            type: types.auth.signIn.failure,
            payload: error,
        });
    }
};

export default signInWithEmailAndPassword;