import * as types from 'constants/types';
import { fetchBlog as fetchBlogApi } from 'api';

export const fetchArticles = () => async (dispatch) => {

    dispatch({
        type: types.blog.get.articles.request,
    });

    try {
        const articles = await fetchBlogApi();

        dispatch({
            type: types.blog.get.articles.success,
            payload: articles,
        });
    } catch (error) {
        dispatch({
            type: types.blog.get.articles.failure,
            payload: error,
        });
    }
};