import * as types from 'constants/types';

export const fetchBlogArticles = () => async (dispatch) => {

    dispatch({
        type: types.blog.get.articles.request,
    });

    try {
        const url = 'https://jsonplaceholder.typicode.com/posts';
        const response = await fetch(url);
        const articles = await response.json();

        dispatch({
            type: types.blog.get.articles.success,
            payload: articles,
        });
    } catch (error) {
        dispatch({
            type: types.blog.get.articles.failure,
            payload: error,
        });
    }
};

export const fetchBlogArticle = (id) => async (dispatch) => {

    dispatch({
        type: types.blog.get.article.request,
    });

    try {
        const url = `https://jsonplaceholder.typicode.com/posts/${id}`;
        const response = await fetch(url);
        const article = await response.json();

        dispatch({
            type: types.blog.get.article.success,
            payload: article,
        });
    } catch (error) {
        dispatch({
            type: types.blog.get.article.failure,
            payload: error,
        });
    }
};

