import * as types from 'constants/types';
import { fetchBlogSingle as fetchBlogArticleApi } from 'api';

const fetchBlogArticle = (slug) => async (dispatch) => {

    dispatch({
        type: types.blog.get.article.request,
    });

    try {
        const article = await fetchBlogArticleApi(slug);

        dispatch({
            type: types.blog.get.article.success,
            payload: article,
        });
    } catch (error) {
        dispatch({
            type: types.blog.get.article.failure,
            payload: error,
        });
    }
};

export default fetchBlogArticle;