import * as types from 'constants/types';

const fetchArticleComments = (postId) => async (dispatch) => {
    dispatch({
        type: types.comments.get.request,
    });

    try {
        const url = `https://jsonplaceholder.typicode.com/comments?postId=${postId}`;
        const response = await fetch(url);
        const comments = await response.json();

        dispatch({
            type: types.comments.get.success,
            payload: comments,
        });
    } catch (error) {
        dispatch({
            type: types.comments.get.failure,
            payload: error,
        });
    }
};

export default fetchArticleComments;