import { fetchBooksApi } from 'api';

export const fetchBooks = () => async (dispatch) => {

    dispatch({
        type: 'FETCH_BOOKS_REQUEST',
    });

    try {
        const books = await fetchBooksApi();

        dispatch({
            type: 'FETCH_BOOKS_SUCCESS',
            payload: books,
        });
    } catch (error) {
        dispatch({
            type: 'FETCH_BOOKS_FAILURE',
            payload: error,
        });
    }
};