import * as types from 'constants/types';
import faker from 'faker';

const data = [...Array(20)].map(() => ({
    id: faker.random.uuid(),
    image: faker.random.image(),
    title: faker.random.words(),
    receivedAt: Date.now(),
}));

const fetchCoursesApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(data), 1000);
});

export const fetchCourses = () => async (dispatch) => {

    dispatch({
        type: types.courses.get.request,
    });

    try {
        const couress = await fetchCoursesApi();

        dispatch({
            type: types.courses.get.success,
            payload: couress,
        });
    } catch (error) {
        dispatch({
            type: types.courses.get.failure,
            payload: error,
        });
    }
};