import * as types from 'constants/types';

import { fetchCoursesApi, fetchCategoriesApi, fetchCourseApi } from 'api';

export const fetchCourses = () => async (dispatch) => {

    dispatch({
        type: 'FETCH_COURSES_REQUEST',
    });

    try {
        const courses = await fetchCoursesApi();

        dispatch({
            type: 'FETCH_COURSES_SUCCESS',
            payload: courses,
        });
    } catch (error) {
        dispatch({
            type: 'FETCH_COURSES_FAILURE',
            payload: error,
        });
    }
};

export const fetchCoursesCategories = () => async (dispatch) => {

    dispatch({
        type: types.courses.get.categories.request,
    });

    try {
        const categories = await fetchCategoriesApi();

        dispatch({
            type: types.courses.get.categories.success,
            payload: categories,
        });
    } catch (error) {
        dispatch({
            type: types.courses.get.categories.failure,
            payload: error,
        });
    }
};

export const fetchCourse = (slug) => async (dispatch) => {

    dispatch({
        type: 'FETCH_COURSES_REQUEST',
    });

    try {
        const course = await fetchCourseApi(slug);

        dispatch({
            type: 'FETCH_COURSE_SUCCESS',
            payload: course,
        });
    } catch (error) {
        dispatch({
            type: 'FETCH_COURSES_FAILURE',
            payload: error,
        });
    }
};