import * as types from 'constants/types';

export const fetchGalleryPhotos = (page) => async (dispatch) => {
    dispatch({
        type: types.gallery.get.images.request,
    });

    try {
        const url = `https://jsonplaceholder.typicode.com/photos?_page=${page}&_limit=10`;
        const response = await fetch(url);
        const images = await response.json();

        dispatch({
            type: types.gallery.get.images.success,
            payload: images,
        });
    } catch (error) {
        dispatch({
            type: types.gallery.get.images.failure,
            payload: error,
        });
    }
};