import * as types from 'constants/types';

export const fetchGalleryPhotos = (page, limit) => async (dispatch) => {

    dispatch({
        type: types.gallery.get.images.request,
    });

    try {
        // const url = `https://jsonplaceholder.typicode.com/photos?_page=${page}&_limit=${limit}`;
        // const url = `https://picsum.photos/v2/list?limit=${limit}`;

        const url = `https://picsum.photos/v2/list?page=${page}&limit=${limit}`;
        const response = await fetch(url);
        const images = await response.json();

        dispatch({
            type: types.gallery.get.images.success,
            payload: images,
        });
    } catch (error) {
        dispatch({
            type: types.gallery.get.images.failure,
            payload: error,
        });
    }
};