import * as types from 'constants/types';
import { fetchMenu as fetchMenuApi } from 'api';

export function fetchMenu() {

    return async (dispatch) => {
        dispatch({
            type: types.menu.start,
        });

        try {
            const menu = await fetchMenuApi();

            dispatch({
                type: types.menu.success,
                payload: menu,
            });
        } catch (err) {
            dispatch({
                type: types.menu.failure,
                payload: err,
                error: true,
            });
        }
    };
};