import * as R from 'ramda';

import movies from 'api/movies.mock.js';
import categories from 'api/categories.mock.js';
import menuAuth from './menu.auth.mock';
import menuNonAuth from './menu.nonAuth.mock';

export const fetchMenuAuth = async () => new Promise((resolve) => resolve(menuAuth));

export const fetchMenuNonAuth = async () => new Promise((resolve) => resolve(menuNonAuth));

export const fetchMovies = async () => new Promise((resolve) => {
    setTimeout(() => {
        resolve(movies);
    }, 1000);
});

export const fetchMovie = async (id) => new Promise((resolve) => {
    const movie = R.find(R.propEq('id', id), movies);
    resolve(movie);
});

export const fetchCategories = async () => new Promise((resolve) => resolve(categories));