import * as R from 'ramda';

import courses from './courses.mock';
import categories from './courses.categories.mock';
import books from './books.mock';
import users from './users.mock';

export const fetchCoursesApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(courses), 3000);
});

export const fetchCategoriesApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(categories), 1000);
});

export const fetchCourseApi = async (id) => new Promise((resolve) => {
    setTimeout(() => resolve(R.find(R.propEq('id', parseInt(id, 10)), courses)), 1000);
});

export const fetchBooksApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(books), 2500);
});

export const fetchUsersApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(users), 1500);
});