import courses from './courses.mock';
import categories from './courses.categories.mock';

export const fetchCoursesApi = async () => new Promise((resolve) => {
    setTimeout(() => resolve(courses), 3000);
});

export const fetchCategoriesApi = () => new Promise((resolve) => {
    setTimeout(() => resolve(categories), 1000);
});

export const fetchCourseApi = (slug) => new Promise((resolve) => {
    console.log(slug);
    console.log(courses.map((course) => course.id === slug));

    setTimeout(() => resolve(courses.find((course) => course.id === slug)), 1000);
});