import React, { useState } from 'react';
import { compose } from 'recompose';

import { withFirebase } from 'components/firebase';

function FormCreatePost({ firebase }) {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [tagList, setTagList] = useState('');

    const onSubmit = (event) => {
        event.preventDefault();

        firebase.doCreatePost()
            .set({
                title,
                description,
                tagList,
            });
    };

    const isInvalid = title === '' || description === '' || tagList === '';

    return (
        <form className="" onSubmit={onSubmit}>
            <div>
                <label htmlFor="title">title</label>
                <input
                    id="title"
                    type="text"
                    placeholder="title"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}
                />
            </div>
            <div>
                <label htmlFor="description">description</label>
                <textarea
                    id="description"
                    type="text"
                    rows="8"
                    placeholder="description"
                    value={description}
                    onChange={(event) => setDescription(event.target.value)}
                />
            </div>
            <div>
                <label htmlFor="tagList">Tag list</label>
                <input
                    id="tagList"
                    type="text"
                    placeholder="tag list"
                    value={tagList}
                    onChange={(event) => setTagList(event.target.value)}
                />
            </div>
            <div>
                <button
                    className="button success"
                    type="submit"
                    disabled={isInvalid}
                >
                    Опубликовать
                </button>
            </div>
        </form>
    );
}

export default compose(withFirebase)(FormCreatePost);