import React from 'react';
import { take } from 'ramda';

import Chip from 'components/Chip';

import './post.scss';

function Post({ title, description, image }) {

    const short = `${take(120, description)}...`;

    return (
        <article className="blog-post">
            <div className="blog-post__thumbnail">
                <span className="blog-post__tag">tag</span>
                <img src={image} alt={title} />
            </div>
            <div className="blog-post__content">
                <div className="blog-post__info">
                    <span>
                        <Chip text="Softwares" className="primary" />
                        <Chip text="Programming" className="danger" />
                    </span>
                    <span>10 June</span>
                </div>
                <h2>{title}</h2>
                <p>{short}</p>
            </div>
        </article>
    );
}

export default Post;