import React, { useEffect } from 'react';
import { shallowEqual, useSelector } from 'react-redux';

import fetchBlog from 'redux/actions/blog';
import useActions from 'hooks/useActions';
import Post from './components/Post';
import Subsicribe from './components/Subsicribe';
import { Link } from 'react-router-dom';

const stickyStyled = {
    position: 'sticky',
    top: '0',
};

function Blog() {

    const { blog } = useSelector(({ blog }) => ({
        blog: blog.data,
    }), shallowEqual);

    const [fetchBlogActionDispatch] = useActions([fetchBlog]);

    useEffect(() => {
        fetchBlogActionDispatch();
    }, [fetchBlogActionDispatch]);

    return (
        <main className="main">
            <div className="container">
                <section className="blog">
                    <h1>Блог</h1>
                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                        1
                        <Link
                            className="button"
                            to="/blog/create"
                        >
                            Create new
                        </Link>
                    </div>
                    <div className="grid">
                        <div className="content">
                            {blog && blog.map((post) => <Post {...post} key={post.id} />)}
                        </div>
                        <div className="" style={{ position: "relative" }}>
                            <div style={stickyStyled}>
                                <Subsicribe />
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        </main>
    );
}

export default Blog;