import React, { useEffect } from 'react';
import { shallowEqual, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

import fetchBlog from 'redux/actions/blog';
import useActions from 'hooks/useActions';
import Post from './components/Post';
import Sidebar from 'components/Sidebar';
import Subsicribe from './components/Subsicribe';

import './blog.scss';

function Blog() {

    const { blog } = useSelector(({ blog }) => ({
        blog: blog.data,
    }), shallowEqual);

    const [fetchBlogActionDispatch] = useActions([fetchBlog]);

    useEffect(() => {
        fetchBlogActionDispatch();
    }, [fetchBlogActionDispatch]);

    return (
        <main className="main">
            <div className="container">
                <section className="blog">
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: '2rem' }}>
                        <h1 className="blog__heading" style={{ margin: '0px' }}>Блог</h1>
                        <Link
                            className="button"
                            to="/blog/new"
                        >
                            Create new
                        </Link>
                    </div>
                    <div className="grid">
                        <div className="content">
                            {blog && blog.map((post) => <Post {...post} key={post.id} />)}
                        </div>
                        <Sidebar>
                            <Subsicribe />
                        </Sidebar>
                    </div>
                </section>
            </div>
        </main>
    );
}

export default Blog;