import React from 'react';

import { withFirebase } from 'components/firebase';

const Link = (props) => <a {...props}>Выход</a>;

function SignOutLink({ firebase }) {

    const onClick = (event) => {
        firebase.doSignOut();
        event.preventDefault();
    };

    return (
        <Link
            to="#"
            onClick={onClick}
        >
            Выход
        </Link>
    );
}

export default withFirebase(SignOutLink);