// window.addEventListener('scroll', debounce(() => {
// Check how far the user has scrolled
// }, 500));

function debounce(callback, wait) {
    let timeout;
    return (...args) => {
        const context = this;
        clearTimeout(timeout);
        timeout = setTimeout(() => callback.apply(context, args), wait);
    };
}

export default debounce;