import React from 'react';
import { Link } from 'react-router-dom';

import Author from '../Author';

import './article.scss';

function Article(props) {

    const { id = 0, title = '', body = '', image = 'https://via.placeholder.com/620x380' } = props;

    return (
        <article className="blog-article">
            <Link to={`/blog/${id}`}>
                <h2 className="blog-article-title">{title}</h2>
            </Link>
            <Author />
            <Link to={`/blog/${id}`}>
                <div className="blog-article-thumbnail">
                    <img src={image} alt={title} />
                </div>
            </Link>
            <p className="blog-article-description">
                {body.substring()}
            </p>
        </article>
    );
}

export default Article;