import React from 'react';
import { Link } from 'react-router-dom';

import './author.scss';

function Author({
    avatar = 'https://robohash.org/imanewuser.png',
    author = 'Stella Johnson',
    position = 'Developer',
    date = 'April, 2020',
}) {

    return (
        <div className="author">
            <div className="avatar">
                <Link to="/user">
                    <img src={avatar} alt={author} />
                </Link>
            </div>
            <div className="body">
                <h4 className="name">
                    <Link to='/user'>{author}</Link>
                </h4>
                <span className="position">{position}<span className="date">{date}</span></span>
            </div>
        </div>
    );
}

export default Author;