import React from 'react';
import { Link } from 'react-router-dom';

import './author.scss';

function Author({ avatar = 'https://robohash.org/imanewuser.png', username = 'Stella Johnson' }) {

    return (
        <div className="author">
            <div className="avatar">
                <Link to="/user">
                    <img src={avatar} alt={username} />
                </Link>
            </div>
            <div className="body">
                <h4 className="name">
                    <Link to='/user'>{username}</Link>
                </h4>
                <span className="position">Developer<span className="date">April, 2020</span></span>
            </div>
        </div>
    );
}

export default Author;