import React, { useState } from 'react';

import './subsicribe.scss';

function Subsicribe() {

    const [email, setEmail] = useState('');
    // const [error, setError] = useState(null);

    const onSubmit = (event) => {

        event.preventDefault();
    };

    const onChange = (event) => {
        setEmail(event.target.value);
    };

    const isInvalid = email === '';

    return (
        <aside className="subsicribe">
            <h3 className="subsicribe__title">Subsicribe</h3>
            <p className="subsicribe__discription">Get the Latest Posts and Article for us On Your Email</p>
            <form onSubmit={onSubmit}>
                <input
                    className="subsicribe__input-field"
                    type="email"
                    placeholder="Enter your email address"
                    value={email}
                    onChange={onChange}
                />
                <input
                    className="subsicribe__submit button"
                    type="submit"
                    value="Subscirbe"
                    disabled={isInvalid}
                />
            </form>
        </aside>
    );
}

export default Subsicribe;