import React, { useEffect } from 'react';
import { useSelector, shallowEqual } from 'react-redux'

import useActions from 'hooks/useActions';
import { fetchMovieActionCreator } from 'actions/movie';

const Movie = ({ match }) => {
    const id = Number(match.params.id);
    const [fetchMovie] = useActions([fetchMovieActionCreator]);
    const movie = useSelector(({ movie }) => movie.current, shallowEqual);

    useEffect(() => {
        fetchMovie(id)
    }, [fetchMovie, id]);

    return (
        <div>Home</div>
    );
};

export default Movie;