import React, { useEffect } from 'react';
import { useSelector, shallowEqual } from 'react-redux';

import useActions from 'hooks/useActions';
import fetchMoviesActionCreator from 'actions/movies';
import fetchCategoriesActionCreator from 'actions/categories';
import { getCategories } from 'selectors';

import './movies.scss';
import { Link } from 'react-router-dom';

const Movies = () => {

    const [fetchMovies, fetchCategories] = useActions([fetchMoviesActionCreator, fetchCategoriesActionCreator]);

    useEffect(() => {
        fetchMovies();
        fetchCategories();
    }, [fetchMovies, fetchCategories]);

    const movies = useSelector(({ movies }) => movies.all, shallowEqual);
    const categories = useSelector(getCategories, shallowEqual);

    const mapCategories = (arrayCategoryId) => {
        let result = categories.filter((category) => arrayCategoryId.includes(category.id));

        return result.map(({ id, title }) => (
            <Link to={`/category/${id}`} key={title}>{title}</Link>
        ));
    };

    return (
        <main>
            <section className='movies'>
                {movies && movies.map((movie, index) => (
                    <article className='movie' key={index}>
                        <div className='image'>
                            <img src={movie.image} alt={movie.title} />
                        </div>
                        <h3><Link to={`/movie/${movie.id}`}>{movie.title}</Link></h3>
                        <nav>
                            {mapCategories(movie.categoryId)}
                        </nav>
                    </article>
                ))}
            </section>
        </main>
    );
};

export default Movies;