import * as types from 'constants/types';

const initialState = {
    isFetching: false,
    articles: {},
    error: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case types.blog.get.articles.request:
            return { ...state, isFetching: true };
        case types.blog.get.articles.success:
            // eslint-disable-next-line no-sequences
            const nextState = payload.reduce((obj, article) => (obj[article.id] = article, obj), {});
            return { ...state, isFetching: false, articles: nextState };
        case types.blog.get.articles.failure:
            return { ...state, isFetching: false, error: payload };
        default:
            return state;
    }
};