import * as types from 'constants/types';

const initialState = {
    isFetching: false,
    article: {},
    error: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case types.blog.get.article.request:
            return { ...state, isFetching: true };
        case types.blog.get.article.success:
            return { ...state, isFetching: false, article: payload };
        case types.blog.get.article.failure:
            return { ...state, isFetching: false, error: payload };
        default:
            return state;
    }
};