import * as types from 'constants/types';

const initialState = {
    isFetching: false,
    comments: [],
    error: false,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case types.comments.get.request:
            return { ...state, isFetching: true };
        case types.comments.get.success:
            return { ...state, isFetching: false, comments: payload };
        case types.comments.get.failure:
            return { ...state, isFetching: false, error: payload };
        default:
            return state;
    }
};