import * as types from 'constants/types';

const initialState = {
    isFetching: false,
    photos: [],
    errors: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case types.gallery.get.images.request:
            return { ...state, isFetching: true };
        case types.gallery.get.images.success:
            return { ...state, isFetching: false, photos: payload };
        case types.gallery.get.images.failure:
            return { ...state, isFetching: false, errors: payload };
        default:
            return state;
    }
};