import { combineReducers } from 'redux';

import blog from 'reducers/blog';
import blogArticle from 'reducers/blogArticle';
import blogArticleComments from 'reducers/blogArticleComments';
import gallery from 'reducers/gallery';

export default combineReducers({
    blog,
    blogArticle,
    blogArticleComments,
    gallery
});