import types from 'redux/constants/types/blog';
import { fetchBlog as fetchBlogApi } from 'api';

const fetchBlog = () => async (dispatch) => {

    dispatch({
        type: types.get.request
    });

    try {
        const blog = await fetchBlogApi();

        dispatch({
            type: types.get.success,
            payload: blog,
        });
    } catch (err) {
        dispatch({
            type: types.get.failure,
            payload: err,
            error: true,
        });
    }
};

export default fetchBlog;