import * as types from 'constants/types';
import { fetchBlog as fetchBlogApi } from 'api';

const fetchBlog = () => async (dispatch) => {

    dispatch({
        type: types.blog.start,
    });

    try {
        const blog = await fetchBlogApi();

        dispatch({
            type: types.blog.success,
            payload: blog,
        });
    } catch (err) {
        dispatch({
            type: types.blog.failure,
            payload: err,
            error: true,
        });
    }
};

export default fetchBlog;