import types from 'redux/constants/types/blog';

import { fetchBlogSingle as fetchBlogSingleApi } from 'api';

const fetchBlogSingle = (slug) => async (dispatch) => {

    dispatch({
        type: types.single.get.request,
    });

    try {
        const single = await fetchBlogSingleApi(slug);

        dispatch({
            type: types.single.get.success,
            payload: single,
        });
    } catch (error) {
        dispatch({
            type: types.single.get.failure,
            payload: error,
            error: true,
        });
    }
};

export default fetchBlogSingle;