import initialState from 'redux/constants/initialState';
import types from 'redux/constants/types/blog';

export default (state = initialState.blog.single, { type, payload }) => {

    switch (type) {
        case types.single.get.request:
            return { ...state, isFetching: true };
        case types.single.get.success:
            return { ...state, isFetching: false, data: payload };
        case types.single.get.failure:
            return { ...state, isFetching: false, error: payload };
        default:
            return state;
    }
};