import initialState from 'redux/constants/initialState';
import types from 'redux/constants/types/comments';

export default (state = initialState.comments, { type, payload }) => {

    switch (type) {
        case types.get.request:
            return { ...state, isFetching: true };

        case types.get.success: {
            const nextState = payload.reduce((obj, row) => (obj[row.id] = row, obj), {});
            return { ...state, isFetching: false, data: nextState };
        }

        case types.get.failure:
            return { ...state, isFetching: false, error: payload };

        case types.create.request: {
            const nextState = Object.assign({}, state.data);
            nextState[payload.id] = payload;
            return { ...state, isFetching: false, data: nextState };
        }

        default:
            return state;
    }
};