import Home from 'pages/Home';
import User from 'pages/User';
import Login from 'pages/Login';
import Blog from 'pages/Blog';
import BlogSingle from 'pages/Blog/components/Single';
import BlogNew from 'pages/Blog/components/New';
import BlogEdit from 'pages/Blog/components/Edit';


const routes = [
    {
        path: '/',
        component: Home,
        exact: true,
    },
    {
        path: '/user',
        component: User,
        exact: true,
    },
    {
        path: '/user/:username',
        component: User,
        exact: true,
    },
    {
        path: '/blog',
        component: Blog,
        exact: true,
    },
    {
        path: '/blog/new',
        component: BlogNew,
        exact: true,
    },
    {
        path: '/blog/:slug',
        component: BlogSingle,
        exact: true,
    },
    {
        path: '/blog/:slug/edit',
        component: BlogEdit,
        exact: true,
    },
    {
        path: '/login',
        component: Login,
        exact: true,
    },
];

export default routes;